import { useEffect } from 'react';
import './App.css';
import { useState } from 'react';

const cardlist = [
  {"path":"/img/1.jpeg" },
  {"path":"/img/2.jpeg" },
  {"path":"/img/3.jpeg" },
  {"path":"/img/4.jpeg" },
  {"path":"/img/5.jpeg" },
  {"path":"/img/6.jpeg" }
]


function App() {

  const [cards,setCards] = useState([]);

  setCards(cardlist);


  return (
    <div className="App">
      <h1>Memory App</h1>
      <button>Oyunu Ba≈ülat</button>
      <div className="card-grid">
        {
          cards.map((card) => {
            <div className="card">
              <img src="{card.path}" alt="" />
              <img src="{../public/img/cover.jpeg}" alt="" />
            </div>
          })
        }
      </div>
    </div>
  );
}

export default App;
