import { useEffect } from 'react';
import './App.css';
import { useState } from 'react';

const cardlist = [
  {"path":"/img/1.jpeg" },
  {"path":"/img/2.jpeg" },
  {"path":"/img/3.jpeg" },
  {"path":"/img/4.jpeg" },
  {"path":"/img/5.jpeg" },
  {"path":"/img/6.jpeg" }
]


function App() {

  const [cards,setCards] = useState([]);

  const prepareCards = () => {

    const sortedCards = [...cardlist,...cardlist ]
      .sort(() => {return 0.5 - Math.random()})
      .map((card) => ({...card,id:Math.random()}));


      setCards(sortedCards);

  }  

  useEffect(() => {
    prepareCards();
  },[]);

  return (
    <div className="continer">
      <h1>Memory App</h1>
      <button onClick={prepareCards}>Oyunu Ba≈ülat</button>
      <div className="card-grid">
        {
          cards.map(card => (
            <div className="card">
              <img className='cardFornt' src={card.path} alt="" />
              <img className='cardback' src="/img/cover.jpeg" alt="" />
            </div>
          ))
        }
      </div>
    </div>
  );
}

export default App;
